    def on_comboBox_Saison_changed(self, index):
        # Fonction appelée lorsque la sélection de la combo box change
        selected_option = self.comboBox_Saison.currentText()
        
        if selected_option == "Inazuma Eleven 1":
            self.FolderPath = os.path.abspath(os.path.join(os.getcwd(), "Joueurs", "Inazuma Eleven 1"))
        if selected_option == "Inazuma Eleven 2":
            self.FolderPath = os.path.abspath(os.path.join(os.getcwd(), "Joueurs", "Inazuma Eleven 2"))
        if selected_option == "Inazuma Eleven 3":
           self.FolderPath = os.path.abspath(os.path.join(os.getcwd(), "Joueurs", "Inazuma Eleven 3"))
        if selected_option == "Inazuma Eleven GO":
           self.FolderPath = os.path.abspath(os.path.join(os.getcwd(), "Joueurs", "Inazuma Eleven GO"))
        if selected_option == "Inazuma Eleven GO Chrono Stone":
           self.FolderPath = os.path.abspath(os.path.join(os.getcwd(), "Joueurs", "Inazuma Eleven GO Chrono Stone"))
        if selected_option == "Inazuma Eleven Go Galaxy":
           self.FolderPath = os.path.abspath(os.path.join(os.getcwd(), "Joueurs", "Inazuma Eleven Go Galaxy"))
        if selected_option == "Inazuma Eleven Ares":
           self.FolderPath = os.path.abspath(os.path.join(os.getcwd(), "Joueurs", "Inazuma Eleven Ares"))
        if selected_option == "Inazuma Eleven Orion":
           self.FolderPath = os.path.abspath(os.path.join(os.getcwd(), "Joueurs", "Inazuma Eleven Orion"))



        self.Joueurs = [os.path.join(self.FolderPath, fichier) for fichier in os.listdir(self.FolderPath) if os.path.isfile(os.path.join(self.FolderPath, fichier)) and fichier.lower().endswith(".png")]
        # Initialiser les listes
        self.FW = []
        self.GK = []
        self.MF = []
        self.DF = []
        self.on_comboBox_type_changed(index = 0)

        # Parcourir la liste des joueurs et les classer
        for joueur in self.Joueurs:
            # Extraire le nom de fichier
            nom_fichier = os.path.basename(joueur)

            # Vérifier la présence de chaque chaîne de caractères et classer le joueur en conséquence
            if "(FW)" in nom_fichier:
                self.FW.append(joueur)
            elif "(GK)" in nom_fichier:
                self.GK.append(joueur)
            elif "(MF)" in nom_fichier:
                self.MF.append(joueur)
            elif "(DF)" in nom_fichier:
                self.DF.append(joueur)

