    def StartRandomisation(self):
        self.offset = 0
        self.pushButton_Start.setEnabled(False)
        self.pushButton_Select1.setEnabled(False)
        self.pushButton_Select2.setEnabled(False)
        self.pushButton_Select3.setEnabled(False)
        self.pushButton_Select4.setEnabled(False)
        self.pushButton_Select5.setEnabled(False)
        _translate = QtCore.QCoreApplication.translate

        # Parcourez la liste des joueurs et mettez à jour les images des labels
        
        self.TotalItems = self.Nb_Cycle * len(self.CurrentList)  # Total des éléments à travers toutes les boucles

        for j in range(self.Nb_Cycle):
            random.shuffle(self.CurrentList)
            for i, self.BaseNom in enumerate(self.CurrentList):
                #print(self.BaseNom)

                # Choisissez le label en fonction de l'index
                if i % 5 == 0:
                    label = self.label_Image1
                    label2 = self.label_Name1
                    label3 = self.label_team1
                elif i % 5 == 1:
                    label = self.label_Image2
                    label2 = self.label_Name2
                    label3 = self.label_team2
                elif i % 5 == 2:
                    label = self.label_Image3
                    label2 = self.label_Name3
                    label3 = self.label_team3
                elif i % 5 == 3:
                    label = self.label_Image4
                    label2 = self.label_Name4
                    label3 = self.label_team4
                else:
                    label = self.label_Image5
                    label2 = self.label_Name5
                    label3 = self.label_team5

                nom_fichier = os.path.basename(self.BaseNom)

                # Définir une expression régulière pour extraire le prénom, le nom et le genre
                if self.comboBox_type.currentText()== "Formation":
                    # Expression régulière
                    pattern = re.compile(r'\((.*?)\) (.+?)\.png')
                    nom_fichier1 = os.path.basename(self.CurrentList[-5])
                    match1 = pattern.match(nom_fichier1)
                    # Effectuer la correspondance avec le nom du fichier
                    match = pattern.match(nom_fichier)
                    # Extraire le prénom, le nom et le genre à partir des groupes capturants
                    team = match.group(1)
                    prenom_nom = match.group(2)
                    # Mettez à jour l'image du label
                    label2.setText(_translate("MainWindow", "{}".format(prenom_nom)))
                    label.setPixmap(QtGui.QPixmap(self.BaseNom))
                    label3.setText(_translate("MainWindow", "{}".format(team)))
                elif self.comboBox_type.currentText() == "Coach":
                    # Expression régulière
                    pattern = re.compile(r'\((.*?)\) (.+?)\.png')            
                    # Effectuer la correspondance avec le nom du fichier
                    match = pattern.match(nom_fichier)
                    # Extraire le prénom, le nom et le genre à partir des groupes capturants
                    team = match.group(1)
                    prenom_nom = match.group(2)
                    # Mettez à jour l'image du label
                    label2.setText(_translate("MainWindow", "{}".format(prenom_nom)))
                    label.setPixmap(QtGui.QPixmap(self.BaseNom))
                    label3.setText(_translate("MainWindow", "{}".format(self.Team.get(team))))
                else:
                    pattern = re.compile(r'\((.*?)\) \((.*?)\) \((.*?)\) (.+?)\.png')
                    match = pattern.match(nom_fichier)
                    # Extraire le prénom, le nom et le genre à partir des groupes capturants
                    team = match.group(1)
                    genre = match.group(3)
                    prenom_nom = match.group(4)
                    # Mettez à jour l'image du label
                    label2.setText(_translate("MainWindow", "{} - {}".format(prenom_nom,genre)))
                    label.setPixmap(QtGui.QPixmap(self.BaseNom))
                    label3.setText(_translate("MainWindow", "{}".format(self.Team.get(team))))

                progress_percentage = (i + len(self.CurrentList) * j) / self.TotalItems

                if progress_percentage < 0.6:
                    delay_factor = 0.1  # Vitesse rapide pour les 70% premiers
                elif progress_percentage < 0.7:
                    delay_factor = 0.3  # Ralentissement progressif pour les 20% suivants
                else:
                    delay_factor = 1000000  # Très lent pour les 10% derniers
                delay = 1/(1 + (self.TotalItems - i - len(self.CurrentList) * j) / delay_factor)

                # Convertir le délai de secondes à millisecondes
                delay_ms = delay /1000

                # Actualisez l'affichage à chaque itération
                print(f"Iteration {1 + (self.TotalItems - i - len(self.CurrentList) * j)}/{(len(self.CurrentList)*self.Nb_Cycle)}, Delay: {delay_ms} ms\t",delay_factor)
                time.sleep(delay_ms)
                QtWidgets.QApplication.processEvents()
        

        if self.comboBox_type.currentText()== "Formation":
            nom_fichier1 = os.path.basename(self.CurrentList[-5])
            match1 = pattern.match(nom_fichier1)
            if match1:
                team = match1.group(1)
                prenom_nom = match1.group(2)
                self.label_Name1.setText(_translate("MainWindow", "{}".format(prenom_nom)))
                self.label_team1.setText(_translate("MainWindow", "{}".format(team)))

